	, END_DATE
	, REG_DATE
	, REG_USER_ID
	, MOD_DATE
	, MOD_USER_ID
	, DEL_FLAG
)
VALUES (
	#{ses.gateCd}
	, #{EV_NUM}
	, #{EV_NM}
	, #{PURCHASE_TYPE}
	, #{EV_TYPE}
	, #{PROGRESS_CD}
	, #{EV_CTRL_USER_ID}
	, #{SCRE_EV_TPL_NUM}
	, #{SITE_EV_TPL_NUM}
	, #{RESULT_ENTER_CD}
	, #{ATT_FILE_NUM}
	, #{VENDOR_CD}
	, #{START_DATE}
	, #{END_DATE}
	, 
	, #{ses.userId}
	, 
	, #{ses.userId}
	, '0'
)

