mapper.select:
SELECT VENDOR_CD , VENDOR_NM , DEAL_SQ_CD , VAATZ_VENDOR_CD
, MAX(REGION_${idx + 1}) AS REGION_${idx + 1}
, MAX(DEAL_TYPE1) AS DEAL_TYPE1 , MAX(DEAL_TYPE2) AS DEAL_TYPE2 , MAX(DEAL_TYPE3) AS DEAL_TYPE3 , MAJOR_ITEM_TEXT , CEO_USER_NM , IRS_NUM , REP_EMAIL , REP_TEL_NUM , ADDR ${query1} , DEAL_START_DATE , REMARK FROM ( SELECT VNGL.VENDOR_CD , VNGL.VENDOR_NM , VNGL.DEAL_SQ_CD , VNGL.VAATZ_VENDOR_CD
, CASE WHEN VNRG.REGION_CD = #{reg.PLANT_CD} THEN '‚óè' ELSE '' END AS REGION_${idx + 1}
, CASE WHEN VNDC.NUM = '1' THEN GETCODENAME(VNDC.GATE_CD, 'M230', VNDC.DEAL_TYPE_CD, #{ses.langCd}) END AS DEAL_TYPE1 , CASE WHEN VNDC.NUM = '2' THEN GETCODENAME(VNDC.GATE_CD, 'M230', VNDC.DEAL_TYPE_CD, #{ses.langCd}) END AS DEAL_TYPE2 , CASE WHEN VNDC.NUM = '3' THEN GETCODENAME(VNDC.GATE_CD, 'M230', VNDC.DEAL_TYPE_CD, #{ses.langCd}) END AS DEAL_TYPE3 , VNGL.MAJOR_ITEM_NM AS MAJOR_ITEM_TEXT , VNGL.CEO_USER_NM , VNGL.IRS_NUM , VNGL.REP_EMAIL , VNGL.REP_TEL_NUM , VNGL.ADDR ' ' VNGL.ADDR_ETC AS ADDR ${query2} , GETGMTDATE(VNGL.DEAL_START_DATE, #{ses.userGmt}, #{ses.systemGmt}, ) AS DEAL_START_DATE , VNGL.REMARK FROM STOCVNGL VNGL LEFT JOIN STOCVNRG VNRG ON(VNGL.GATE_CD = VNRG.GATE_CD AND VNGL.VENDOR_CD = VNRG.VENDOR_CD AND VNRG.DEL_FLAG = '0') LEFT JOIN( SELECT * FROM ( SELECT GATE_CD , VENDOR_CD , DEAL_TYPE_CD , DEL_FLAG , ROW_NUMBER() OVER( PARTITION BY VENDOR_CD ORDER BY VENDOR_CD, DEAL_TYPE_CD ASC) NUM FROM STOCVNDC WHERE GATE_CD = #{ses.gateCd}
AND VENDOR_CD #{VENDOR_CD}
AND GETVENDORNAME(#{ses.gateCd},VENDOR_CD,#{ses.langCd}) #{VENDOR_NM}
) A
WHERE A.NUM <= '3'
) VNDC ON(VNGL.GATE_CD = VNDC.GATE_CD AND VNGL.VENDOR_CD = VNDC.VENDOR_CD AND VNDC.DEL_FLAG = '0') LEFT JOIN( SELECT * FROM ( SELECT GATE_CD , VENDOR_CD , SQ_GRADE_CD , EV_SCORE , CERT_NUM , MAIN_EV_INS_NM , REG_DATE , DEAL_TYPE_CD , DEL_FLAG , ROW_NUMBER() OVER( PARTITION BY VENDOR_CD ORDER BY REG_DATE DESC ) AS NUM FROM STOCEVSQ WHERE GATE_CD = #{ses.gateCd}
AND VENDOR_CD #{VENDOR_CD}
AND GETVENDORNAME(#{ses.gateCd},VENDOR_CD,#{ses.langCd}) #{VENDOR_NM}
GROUP BY GATE_CD , VENDOR_CD , SQ_GRADE_CD , EV_SCORE , CERT_NUM , MAIN_EV_INS_NM , REG_DATE , DEAL_TYPE_CD , DEL_FLAG ) B
WHERE B.NUM <= '3'
) EVSQ ON(VNGL.GATE_CD = EVSQ.GATE_CD AND VNGL.VENDOR_CD = EVSQ.VENDOR_CD AND EVSQ.DEL_FLAG = '0') WHERE VNGL.GATE_CD = #{ses.gateCd} AND VNGL.PROGRESS_CD = 'E' AND VNGL.ITEM_PURCHASE_FLAG = '1' AND (VNGL.DEAL_CLOSE_DATE, ' ') = ' ' AND VNGL.VENDOR_TYPE IN ('B', 'P')
AND VNGL.VENDOR_CD #{VENDOR_CD}
AND VNGL.VENDOR_NM #{VENDOR_NM}
AND VNGL.CEO_USER_NM #{CEO_USER_NM}
AND VNGL.IRS_NUM #{IRS_NUM}
AND VNRG.REGION_CD = #{REGION_CD}
AND VNDC.DEAL_TYPE_CD = #{DEAL_TYPE_CD}
AND VNGL.REG_DATE BETWEEN #{START_DATE} AND #{END_DATE}
AND VNGL.REG_TYPE = #{REG_TYPE}
AND VNGL.MAJOR_ITEM_NM #{MAJOR_ITEM_NM}
AND VNGL.DEAL_SQ_CD = #{DEAL_SQ_CD}
) A GROUP BY VENDOR_CD , VENDOR_NM , DEAL_SQ_CD , VAATZ_VENDOR_CD , MAJOR_ITEM_NM , CEO_USER_NM , IRS_NUM , REP_EMAIL , REP_TEL_NUM , ADDR , DEAL_START_DATE , REMARK

mapper.insert:
UPDATE STOCVNGL
	SET MOD_DATE = 
	  , MOD_USER_ID = #{ses.userId}
	  , DEAL_START_DATE = #{DEAL_START_DATE}
	  , REMARK = #{REMARK}
 WHERE GATE_CD = #{ses.gateCd}
 	AND VENDOR_CD = #{VENDOR_CD}
