mapper.select:
SELECT MASTER.PROGRESS_CD ,GETCODENAME( #{ses.gateCd},'M122',MASTER.PROGRESS_CD,#{ses.langCd}) AS PROGRESS_NM ,MASTER.EV_CTRL_USER_ID ,GETUSERINFO( #{ses.gateCd},MASTER.EV_CTRL_USER_ID,'USER_NM') AS EV_CTRL_USER_NM ,MASTER.EV_NUM ,MASTER.EV_NUM AS HID_EV_NUM ,MASTER.VENDOR_CD ,GETVENDORNAME( #{ses.gateCd},MASTER.VENDOR_CD,#{ses.langCd}) AS VENDOR_NM ,EVVU.PROGRESS_CD AS EV_PROGRESS_CD ,GETCODENAME( #{ses.gateCd},'M124',EVVU.PROGRESS_CD,#{ses.langCd}) AS EV_PROGRESS_NM ,EVVU.EV_USER_ID ,GETUSERINFO( #{ses.gateCd},EVVU.EV_USER_ID,'USER_NM') AS EV_USER_NM ,EVVU.EVAL_SCORE AS EVAL_SCORE ,MASTER.SCRE_EV_TPL_NUM AS EV_TPL_NUM ,EVTM.EV_TPL_SUBJECT , GETGMTDATE(MASTER.REQUEST_DATE, #{ses.userGmt}, #{ses.systemGmt}, #{ses.dateValueFormat}) AS REQUEST_DATE , GETGMTDATE(MASTER.REG_DATE, #{ses.userGmt}, #{ses.systemGmt}, #{ses.dateValueFormat}) AS REG_DATE ,MASTER.PURCHASE_TYPE ,GETCODENAME( #{ses.gateCd},'M197',MASTER.PURCHASE_TYPE,#{ses.langCd}) AS PURCHASE_TYPE_NM ,MASTER.EV_TYPE ,GETCODENAME( #{ses.gateCd},'M186',MASTER.EV_TYPE,#{ses.langCd}) AS EV_TYPE_NM ,MASTER.EV_NM ,MASTER.RESULT_ENTER_CD ,MASTER.COMPLETE_DATE ,( (SELECT EV_USER_ID FROM STOCEVVU WHERE GATE_CD = MASTER.GATE_CD AND EV_NUM = MASTER.EV_NUM AND SG_NUM = ' ' AND DEL_FLAG = '0' AND REP_FLAG = '1' AND EV_USER_ID = #{ses.userId} ),'-') AS REP_EV_USER_ID FROM STOCEVVM MASTER INNER JOIN STOCEVVU EVVU ON MASTER.EV_NUM = EVVU.EV_NUM AND EVVU.DEL_FLAG = '0' AND MASTER.GATE_CD = EVVU.GATE_CD INNER JOIN STOCEVTM EVTM ON MASTER.SITE_EV_TPL_NUM = EVTM.EV_TPL_NUM AND MASTER.GATE_CD = EVTM.GATE_CD AND EVTM.EV_TPL_TYPE_CD = 'S' AND EVTM.DEL_FLAG = '0' WHERE MASTER.GATE_CD = #{ses.gateCd} AND MASTER.DEL_FLAG = '0' AND MASTER.REG_DATE BETWEEN #{REQ_START_DATE}, 'YYYYMMDD HH24:MI:SS') AND #{REQ_END_DATE}, 'YYYYMMDD HH24:MI:SS')
AND MASTER.PROGRESS_CD = #{PROGRESS_CD}
AND GETUSERNAME(MASTER.GATE_CD, MASTER.EV_CTRL_USER_ID, #{ses.langCd}) #{EV_CTRL_USER_NM}
AND GETUSERNAME(EVVU.GATE_CD, EVVU.EV_USER_ID, #{ses.langCd}) #{EV_USER_NM}
AND GETVENDORNAME(#{ses.gateCd},MASTER.VENDOR_CD,#{ses.langCd}) #{VENDOR_NM}
AND MASTER.EV_TYPE = #{EV_TYPE}
ORDER BY MASTER.EV_NUM DESC

     SELECT COUNT(*) AS CNT
		FROM STOCEVVU
	  WHERE EV_NUM     	=  #{HID_EV_NUM}
		   AND REP_FLAG   	= '1'
		   AND DEL_FLAG   	= '0'
		   AND GATE_CD    	=  #{ses.gateCd}
		   AND EV_USER_ID 	=  #{CHK_USER}
